@mixin webFont($fontSize) {
  font-family: "Open Sans", sans-serif;
  font-size: $fontSize;
}
header {
  @include webFont(2rem);
}

@mixin radial-gradient($direction, $colors...) {
  background-color: white;
  background: radial-gradient($direction, $colors);
}
.box {
  @include radial-gradient(ellipse at top, orange, purple);
}

