@each $animal in puma, sea-slug, egret, salamander {
  .#{$animal}-icon {
    background-image: url('/images/#{$animal}.png');
  }
}

@mixin border($color, $side) {
  @if $side == all {
    border: 3px solid $color;
  } @else if $side == none {
    border: none;
  } @else {
    border-#{$side}: 3px solid $color;
  }   }

